	void EvmTest2()
	{
		// demo voting contract
		static const char szCode[] = "";

		MyProcessor evm;

		MyProcessor::Address aContract, aOwner;
		ECC::GenRandom(aContract);
		ECC::GenRandom(aOwner);

		{
#pragma pack (push, 1)
			struct MyArgs
			{
				Evm::Word m_ArrMark;
				Evm::Word m_Count;
				Evm::Word m_pszName[2];
			} myArgs;
#pragma pack (pop)

			myArgs.m_ArrMark = 0x20u;
			myArgs.m_Count = _countof(myArgs.m_pszName);
			myArgs.m_pszName[0] = Zero;
			myArgs.m_pszName[1] = Zero;
			memcpy(myArgs.m_pszName[0].m_pData, "Hello", 5);
			memcpy(myArgs.m_pszName[1].m_pData, "world!", 6);

			evm.InitCaller(aOwner);
			verify_test(evm.Construct(aContract, szCode, _countof(szCode) - 1, &myArgs, sizeof(myArgs)));
		}


		MyProcessor::Address aVoter;
		ECC::GenRandom(aVoter);

		{
#pragma pack (push, 1)
			struct MyMethod
				:public Evm::Processor::Method
			{
				Evm::Word m_wVoter;
			} myArg;
#pragma pack (pop)

			myArg.SetSelector("giveRightToVote(address)");
			myArg.m_wVoter = aVoter.ToWord();

			evm.InitCaller(aOwner);
			verify_test(evm.RunMethod_T(aContract, myArg));
			verify_test(!evm.m_RetVal.m_Blob.n);
		}

		{
#pragma pack (push, 1)
			struct MyMethod
				:public Evm::Processor::Method
			{
				Evm::Word m_iVote;
			} myArg;
#pragma pack (pop)

			myArg.SetSelector("vote(uint256)");
			myArg.m_iVote = 1u;

			evm.InitCaller(aVoter);
			verify_test(evm.RunMethod_T(aContract, myArg));
			verify_test(!evm.m_RetVal.m_Blob.n);
		}

		{
			Evm::Processor::Method myArg;
			myArg.SetSelector("winningProposal()");

			evm.InitCaller(aVoter);
			verify_test(evm.RunMethod_T(aContract, myArg));

			verify_test(sizeof(Evm::Word) == evm.m_RetVal.m_Blob.n);
			const auto& wRes = *(const Evm::Word*)evm.m_RetVal.m_Blob.p;
			verify_test(Evm::Word(1u) == wRes);
		}

		{
			Evm::Processor::Method myArg;
			myArg.SetSelector("winnerName()");

			evm.InitCaller(aVoter);
			verify_test(evm.RunMethod_T(aContract, myArg));
			verify_test(sizeof(Evm::Word) == evm.m_RetVal.m_Blob.n);
		}

	}

